import logo from './logo.svg';
import './App.css';
import GameMain from './components/GameMain';
import Home from './components/Home';
import Selector from './components/Selector';
import { useState } from 'react';


function App() {
  const [state, setState] = useState('home');

//selector stuff
    const [grade, setGrade] = useState(5);
    const [number, setNumber] = useState(10);

    const handleGradeInputChange = (event) => {
        setGrade(event.target.value);
      }

    const handleNumberInputChange = (event) => {
       setNumber(event.target.value);
     }

  if (state=='home'){
    return(
      <div className="App">
          <header className="App-header">
            <p>
              edurace
            </p>
            <button onClick={() => setState('selector')}>Start!</button>
          </header>
        </div>
    );}
  else if (state=='selector'){
    <div className="App">
          <header className="App-header">
            <label htmlFor="gradeInput">Grade:    </label>
            <input type="text" id ="gradeInput" value={grade} onChange={handleGradeInputChange}/>
            <label htmlFor="numberInput">Number:    </label>
            <input type="text" id ="numberInput" value={number} onChange={handleNumberInputChange}/>

            <button onClick={() => setState('game')}>Play!</button>
          </header>
        </div>
  }
  else if (state=='game'){
    return(<GameMain/>);
  }
}

export default App;
