import { useState } from 'react';
import '../App.css';
import TakeAnswer from './TakeAnswer';
import RespondAnswer from './RespondAnswer';

function GameMain({moveNext}) {
    const dict = {0:["question1","a"],1:["question2","a"],2:["question3","a"],3:["question4","a"],4:["question5","a"],5:["question6","a"],6:["question7","a"],7:["question8","a"]};
    
    const maxNumberWrong = Math.round(0.25*Object.keys(dict).length);
  
    const [numWrong, setNumWrong] = useState(0);
  
    const [question, setQuestion] = useState(dict); //GPT Code will go here, we will change the dict to be generated questions
  
    const [idx, setIdx] = useState(0);
  
    const [correct,setCorrect] = useState(false);
  
    const [won,setWon] = useState(false);
  
    const [died, setDied] = useState(false);

    const [submitOpen, setSubmitOpen] = useState(true);
  
    const [inputValue,setInputValue] = useState("");
  
    const handleInputChange = (event) => {
      setInputValue(event.target.value);
    };
    const handleSubmit = () => {
      onSubmit(inputValue);
      setInputValue('');
    };
  
    const onSubmit = () => {
      if (inputValue==dict[idx][1]) {
        setCorrect(true);
      }
      else {
        setCorrect(false);
        setNumWrong(numWrong+1);
      }
  
      if (numWrong > maxNumberWrong) {
        setDied(true);
      }
      
      if (idx==Object.keys(dict).length-1){
        setWon(true);
      }
      
      setSubmitOpen(false);
      setTimeout(() => {
        setSubmitOpen(true);
        setIdx(idx+1);
      }, 1000);
    }
  
  //TODO, ADD FUNCTIONALITY TO THE BUTTON
    if(died==true) {
      return(
        <div className="App">
          <header className="App-header">
            <p>
              Your car tragically exploded due to tomfoolery
            </p>
            <button onClick={moveNext}>Play Again!</button>
          </header>
        </div>
      );
  
    }
    else if (won!=true) {
    return (
      <div className="App">
        <header className="App-header">
          <p>
            Question: {dict[idx][0]}
          </p>
          {submitOpen ? 
          <TakeAnswer inputValue={inputValue} handleSubmit={handleSubmit} handleInputChange={handleInputChange}/> : 
          <RespondAnswer quality = {correct}/>}
        </header>
      </div>
    );}
    else{
      return(
        <div className="App">
          <header className="App-header">
            <p>
              Congrats! You won with score {Object.keys(dict).length - numWrong} / {Object.keys(dict).length}
            </p>
            <button onClick={moveNext}>Play Again!</button>
          </header>
        </div>
      );
    }
  }
  
  export default GameMain;
